// Procura pelos markers de JSON
const startMarker = '--- JSON OUTPUT START ---';
const endMarker = '--- JSON OUTPUT END ---';

const jsonStart = output.indexOf(startMarker);
const jsonEnd = output.indexOf(endMarker);

